import { useState, useEffect } from 'react';
import './App.css';
import logo from './logo.svg';

// Custom hook for tracking search history
function useSearchHistory(maxItems = 5) {
  const [history, setHistory] = useState([]);

  const addToHistory = (query) => {
    if (!query || history.includes(query)) return;
    const newHistory = [query, ...history.filter(item => item !== query)].slice(0, maxItems);
    setHistory(newHistory);
    localStorage.setItem('searchHistory', JSON.stringify(newHistory));
  };

  useEffect(() => {
    try {
      const savedHistory = JSON.parse(localStorage.getItem('searchHistory')) || [];
      setHistory(savedHistory);
    } catch (error) {
      console.error('Error loading search history:', error);
    }
  }, []);

  const clearHistory = () => {
    setHistory([]);
    localStorage.removeItem('searchHistory');
  };

  return { history, addToHistory, clearHistory };
}

function SearchBar({ onSearch, placeholder, showHistory = false }) {
  const [query, setQuery] = useState('');
  const [showSuggestions, setShowSuggestions] = useState(false);
  const { history, addToHistory } = useSearchHistory();

  const handleSearch = (e) => {
    e.preventDefault();
    if (query.trim()) {
      onSearch(query);
      addToHistory(query.trim());
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setQuery(suggestion);
    onSearch(suggestion);
    setShowSuggestions(false);
  };

  return (
    <div className="search-container">
      <form onSubmit={handleSearch} className="search-bar">
        <div className="search-input-wrapper">
          <input
            type="text"
            placeholder={placeholder}
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onFocus={() => showHistory && setShowSuggestions(true)}
            onBlur={() => setTimeout(() => setShowSuggestions(false), 200)}
            className="search-input"
          />
          {showSuggestions && history.length > 0 && (
            <div className="search-suggestions">
              <div className="suggestions-header">
                <span>Recent Searches</span>
              </div>
              <ul className="suggestions-list">
                {history.map((item, index) => (
                  <li 
                    key={index} 
                    className="suggestion-item"
                    onClick={() => handleSuggestionClick(item)}
                  >
                    <span className="suggestion-text">{item}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
        <button type="submit" className="search-button">Search</button>
      </form>
    </div>
  );
}

