function Pagination({ currentPage, totalPages, onPageChange }) {
  // Calculate range of page numbers to display
  const getPageRange = () => {
    const range = [];
    const maxVisiblePages = 5;

    if (totalPages <= maxVisiblePages) {
      // If total pages is less than or equal to max visible, show all pages
      for (let i = 1; i <= totalPages; i++) {
        range.push(i);
      }
    } else {
      // Always include first page
      range.push(1);

      // Calculate middle range
      let start = Math.max(2, currentPage - 1);
      let end = Math.min(totalPages - 1, currentPage + 1);

      // Adjust to show 3 pages in the middle
      if (end - start + 1 < 3) {
        if (start === 2) {
          end = Math.min(totalPages - 1, end + 1);
        } else if (end === totalPages - 1) {
          start = Math.max(2, start - 1);
        }
      }

      // Add ellipsis if needed before middle range
      if (start > 2) {
        range.push('...');
      }

      // Add middle range
      for (let i = start; i <= end; i++) {
        range.push(i);
      }

      // Add ellipsis if needed after middle range
      if (end < totalPages - 1) {
        range.push('...');
      }

      // Always include last page
      range.push(totalPages);
    }

    return range;
  };

  return (
    <div className="pagination">
      <button 
        onClick={() => onPageChange(currentPage - 1)} 
        disabled={currentPage === 1}
        className="pagination-button"
      >
        &laquo; Previous
      </button>

      <div className="page-numbers">
        {getPageRange().map((item, index) => (
          item === '...' ? (
            <span key={`ellipsis-${index}`} className="ellipsis">...</span>
          ) : (
            <button
              key={`page-${item}`}
              onClick={() => onPageChange(item)}
              className={`page-number ${currentPage === item ? 'active' : ''}`}
            >
              {item}
            </button>
          )
        ))}
      </div>

      <button 
        onClick={() => onPageChange(currentPage + 1)} 
        disabled={currentPage === totalPages}
        className="pagination-button"
      >
        Next &raquo;
      </button>
    </div>
  );
}

function FilterBar({ onFilterChange, activeFilters, availableFilters }) {
  return (
    <div className="filter-bar">
      <div className="filter-label">Filter by:</div>
      <div className="filter-options">
        {Object.entries(availableFilters).map(([key, options]) => (
          <div key={key} className="filter-group">
            <select 
              className="filter-select"
              onChange={(e) => onFilterChange(key, e.target.value)}
              value={activeFilters[key] || ''}
            >
              <option value="">{key.charAt(0).toUpperCase() + key.slice(1)}</option>
              {options.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>
        ))}
      </div>
      <button 
        className="filter-clear"
        onClick={() => Object.keys(activeFilters).forEach(key => onFilterChange(key, ''))}
      >
        Clear All
      </button>
    </div>
  );
}

