function PredictionPage({ category, onPredict, isLoading }) {
  const [predictionMade, setPredictionMade] = useState(false);
  const [sampleText, setSampleText] = useState('');
  const [showHint, setShowHint] = useState(false);

  const handlePredict = (query) => {
    onPredict(query);
    setPredictionMade(true);
  };

  const sampleTexts = [
    "This study explores the relationship between neural networks and cognitive development in children ages 5-10.",
    "New approaches to sustainable energy production using solar panels in urban environments.",
    "Analysis of recent economic trends in developing countries following the COVID-19 pandemic."
  ];

  const useSampleText = (index) => {
    setSampleText(sampleTexts[index]);
    setTimeout(() => {
      handlePredict(sampleTexts[index]);
    }, 100);
  };

  // Calculate prediction confidence visualization
  const getConfidenceVisual = () => {
    // This would normally come from the API
    const confidence = 0.72; // Sample confidence score

    const segments = 5;
    const filledSegments = Math.round(confidence * segments);

    return (
      <div className="confidence-meter">
        {Array(segments).fill().map((_, i) => (
          <div 
            key={i} 
            className={`confidence-segment ${i < filledSegments ? 'filled' : ''}`}
          />
        ))}
        <span className="confidence-percentage">{Math.round(confidence * 100)}%</span>
      </div>
    );
  };

  return (
    <div className="page-container prediction-page">
      <h1 className="page-title">Category Prediction</h1>
      <p className="page-description">
        Enter text to predict its category using our machine learning model.
      </p>

      <div className="prediction-form">
        <div className="sample-text-section">
          <p>Try one of our sample texts:</p>
          <div className="sample-buttons">
            {sampleTexts.map((text, index) => (
              <button 
                key={index} 
                className="sample-button"
                onClick={() => useSampleText(index)}
              >
                Sample {index + 1}
              </button>
            ))}
          </div>
        </div>

        <textarea
          className="prediction-textarea"
          placeholder="Enter or paste text to categorize..."
          value={sampleText}
          onChange={(e) => setSampleText(e.target.value)}
          onFocus={() => setShowHint(true)}
          onBlur={() => setShowHint(false)}
          rows={6}
        />

        {showHint && (
          <div className="input-hint">For best results, enter at least 50 words.</div>
        )}

        <button 
          className="predict-button" 
          onClick={() => handlePredict(sampleText)}
          disabled={!sampleText.trim() || isLoading}
        >
          {isLoading ? 'Processing...' : 'Predict Category'}
        </button>
      </div>

      {isLoading ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p className="loading-text">Analyzing text...</p>
        </div>
      ) : (
        predictionMade && (
          <div className="prediction-result-container">
            <h2 className="prediction-header">Prediction Results</h2>

            <div className="prediction-card">
              <div className="prediction-main">
                <div className="prediction-label">Category:</div>
                <div className="prediction-value">{category || 'No category detected'}</div>
                {getConfidenceVisual()}
              </div>

              <div className="prediction-metrics">
                <div className="metric">
                  <div className="metric-label">Accuracy</div>
                  <div className="metric-value">67.2%</div>
                  <div className="metric-bar">
                    <div className="metric-progress" style={{ width: '67.2%' }}></div>
                  </div>
                </div>
                <div className="metric">
                  <div className="metric-label">F1 Score</div>
                  <div className="metric-value">0.661</div>
                  <div className="metric-bar">
                    <div className="metric-progress" style={{ width: '66.1%' }}></div>
                  </div>
                </div>
              </div>

              <div className="prediction-categories">
                <h4>Related Categories</h4>
                <div className="category-tags">
                  <span className="category-tag primary">{category}</span>
                  <span className="category-tag">Research</span>
                  <span className="category-tag">Academic</span>
                </div>
              </div>

              <div className="prediction-note">
                <p><strong>Note:</strong> These metrics represent the overall model performance, not the confidence of this specific prediction.</p>
                <p>Want to understand how this prediction works? <a href="#" className="learn-more">Learn more</a> about our model.</p>
              </div>
            </div>
          </div>
        )
      )}
    </div>
  );
}

